CREATE TABLE dancer (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(255));
CREATE TABLE event (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(100), city VARCHAR(100), state VARCHAR(100));
CREATE TABLE song (id INTEGER PRIMARY KEY AUTOINCREMENT, title VARCHAR(100), artist VARCHAR(100));
CREATE TABLE tag (id INTEGER PRIMARY KEY AUTOINCREMENT, title VARCHAR(255) NOT NULL, category VARCHAR(50) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE video (id INTEGER PRIMARY KEY AUTOINCREMENT, url VARCHAR(255), youtube_id VARCHAR(40) NOT NULL UNIQUE, youtube_title VARCHAR(100), youtube_description VARCHAR(1000), published INTEGER DEFAULT '0' NOT NULL, year INTEGER, event_id INTEGER, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE video_dancer (video_id INTEGER, dancer_id INTEGER, PRIMARY KEY(video_id, dancer_id));
CREATE TABLE video_song (video_id INTEGER, song_id INTEGER, PRIMARY KEY(video_id, song_id));
CREATE TABLE video_tag (video_id INTEGER, tag_id INTEGER, PRIMARY KEY(video_id, tag_id));
